    /**
     * Test of toByteArray method, of class Converter. 
     */
    @Test
    public void testToByteArray_File()
    {
        System.out.println("-----------------------------------------------");
        System.out.println("toByteArray(File)");
        
        String filename = "C:\\Users\\user\\Desktop\\sample_bytes.xml";
        
        if (!io.File.exists(filename)) 
        {
            String hex = "";
            byte[] _bytes = Converter.toByteArray(hex);
            io.File.write(filename, _bytes);
        }
        
        byte[] result = Converter.toByteArray(new java.io.File(filename));
        
        if (result == null) fail("Something's wrong");
        else
        {
            if (result.length <= 0) fail("Something's wrong");
            else System.out.println("Result from toByteArray(File) : \n" + new String(result));
        }
    }
    
    /**
     * Test of toByteArray method, of class Converter.
     */
    @Test
    public void testToByteArrayAndToHexadecimalString()
    {
        System.out.println("-----------------------------------------------");
        System.out.println("toByteArray(String)");
        String hex = "";
        
        byte[] result = Converter.toByteArray(hex);
        String toString = new String(result);
        System.out.println("Byte result : " + toString);
        
        System.out.println("-----------------------------------------------");
        System.out.println("toHexadecimalString(byte[])");
        
        String toHex = Converter.toHexadecimalString(result);
        System.out.println("Reversed to Hex : " + toHex);     
        
        if (!toHex.equals(hex.toUpperCase())) fail("Something's wrong");
    }
    
    /**
     * Test of tooByteArray method, of class Converter.
     */
    @Test
    public void testToByteArray_Image()
    {
        System.out.println("-----------------------------------------------");
        System.out.println("toByteArray(Image)");
        
        BufferedImage image = null;
        
        // From file
        try
        { image = ImageIO.read(new java.io.File("C:\\Users\\user\\Desktop\\cross-red.png")); }
        catch (IOException ex)
        { fail(ex.getMessage()); }
        
        byte[] result = Converter.toByteArray(image);
        if (result == null) fail("Something's wrong");
        else System.out.println("Array lenght of converted image : " + result.length);
    
        // From instantiated image
        String hex = "";
        Image _imageFromHex = Converter.toImage(hex);
        
        if (_imageFromHex == null) fail("Something's wrong");
        
        result = Converter.toByteArray(_imageFromHex);
        if (result == null) fail("Something's wrong");
        else
        {
            Image _fromByteArray = Converter.toImage(result);
            if (_fromByteArray == null) fail("Something's wrong");
            
            // Check if result matches with the instantiated image
            
            int _ht1 = 0; int _ht2 = 0;
            if (_imageFromHex != null) _ht1 = _imageFromHex.getHeight(null);
            if (_fromByteArray != null) _ht2 = _fromByteArray.getHeight(null);
            
            assertEquals(_ht1, _ht2);
            
            int _wd1 = 0; int _wd2 = 0;
            if (_imageFromHex != null) _wd1 = _imageFromHex.getWidth(null);
            if (_fromByteArray != null) _wd2 = _fromByteArray.getWidth(null);
            
            assertEquals(_wd1, _wd2);
            
            System.out.println("Image dimension of image from byte array result : " + _wd2 + ", " + _ht2);
        }
    }
    
